import React from 'react'
import s from './paginate.module.css';
import cn from "classnames"
function Paginate({setCurrentPage, currentPage, currentCards}) {


// заполнение массива нужным количеством страниц
  let startPage = 0;
  let endPage = 0;
  const pageNumbers = [];
  for (let i = 1; i <= currentCards; i++) {
    pageNumbers.push(i);
  }
//опреляем как будут отображать страницы 
  if (currentPage === 1 || currentPage === 2 ) {
    startPage = 0;
    endPage = 5;
  } else if (
    currentPage === currentCards ||
    currentPage === currentCards - 1 || currentPage === currentCards - 2
  ) {
    startPage = currentCards - 5;
    endPage = currentCards;
  } else if (currentPage > 2) {
    startPage = currentPage - 3;
    endPage = currentPage + 2;
  }

  const btnClass = cn('.btn_paginate', {
    'active':  currentPage
  })
  console.log( btnClass);
  return (
      <div>
        {currentPage === 1 ? (
        <button
          className={s.btn_last_first }
            disabled={true}
            onClick={() => setCurrentPage(pageNumbers[pageNumbers[0] - 1])}
          >
            First
          </button>
        ) : (
          <button
            className={s.btn_last_first_hover}
            onClick={() => setCurrentPage(pageNumbers[pageNumbers[0] - 1])}
          >
            First
          </button>
          )}
          {pageNumbers.slice(startPage, endPage).map((number) => (
            <button
              className={btnClass}
              className={classNames("page-link", {"page-link-active": number === currentPage})}
              // className={number === currentPage ? { s.btn_paginate_active } : { s.btn_paginate }}
              // className={classNames("btn_paginate", {"btn_paginate_active": number === currentPage})}
              key={number}
              onClick={() => setCurrentPage(number)}

          >
            {number}
          </button>
          ))}
          {currentPage === currentCards ? (
        <button
          className={s.btn_last_first }
            disabled={true}
            onClick={() => setCurrentPage(pageNumbers.length)}
          >
            Last
          </button>
        ) : (
          <button
            className={s.btn_last_first_hover}
            onClick={() => setCurrentPage(pageNumbers.length)}
          >
            Last
          </button>
        )}
    </div>

  )
}

export default Paginate
